///////////types////////

export const GET_USER = "GET_USER";
export const SET_USER = "SET_USER";

/////////////////

////////action creators//////

export const getUser = () => ({
  type: GET_USER,
});

export const setUser = (user) => ({
  type: SET_USER,
  user,
});

/////////////////

////////initial state//////

const initialState = {
  user: undefined,
};
////////////////

//////////reducer//////////
export default (state = initialState, action) => {
  switch (action.type) {
    case SET_USER:
      const { user } = action;
      return { ...state, user };
    default:
      return state;
  }
};
////////////////////
